package com.hve.batch;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.boot.test.web.client.TestRestTemplate;import org.springframework.http.RequestEntity;import org.springframework.http.ResponseEntity;import java.net.URI;@SpringBootTest(classes = HveApplication.class,        webEnvironment = SpringBootTest.WebEnvironment.RANDOM_PORT)public class HveApplicationTest {    @Autowired    private TestRestTemplate rest;    @Test    public void toUpperCaseTest() throws Exception {        ResponseEntity<String> result = this.rest.exchange(                RequestEntity.post(new URI("/toUpperCase")).body("hello"), String.class);        Assertions.assertTrue(result.getBody().equals("HELLO"));    }    @Test    public void getMessageTest() throws Exception {        ResponseEntity<String> result = this.rest.exchange(RequestEntity.get(new URI("/getMessage")).build(),String.class);        Assertions.assertTrue(result.getBody().equals("Welcome to HVE app"));    }}